sys_variable_value.value:
(function execute(inputs, outputs) {
	var invoice = inputs.invoice;
  	var tableName = 'x_tsigh_cmb_client_invoice';
	var util = new x_tsigh_cmb.ClientInvoiceImage();
	var template = util.getHTML(invoice.sys_id);


  	var hfInfo = new Object();
    hfInfo["PageSize"] = "A4";
    hfInfo["GeneratePageNumber"] = "true";
    hfInfo["TopOrBottomMargin"] = "24";
    hfInfo["LeftOrRightMargin"] = "12";
	var v = new sn_pdfgeneratorutils.PDFGenerationAPI;
	var result = v.convertToPDFWithHeaderFooter(template, tableName, invoice.sys_id, invoice.number, hfInfo);

	if (result.status == "success") {
    	outputs.attachment_file = result['attachment_id'];
	}
  	outputs.previous_attachment_sysid = invoice.invoice_file;

})(inputs, outputs);

