sys_variable_value.value:
(function execute(inputs, outputs) {
var invoice = inputs.invoice;
var templateName = inputs.templateName;

var template = gs.getProperty('x_tsigh_cmb.order_template');
var tableName = invoice.getTableName();

template = template.replace('segment_id', invoice.number);
template = template.replace('rfs_date', invoice.invoice_date);
template = template.replace('date_of_order', invoice.sys_created_on);
template = template.replace('otc', invoice.total);
template = template.replace('mrc', invoice.total_net_value);

var lines = new GlideRecord('customer_contact');
lines.addQuery('u_billing_profile', invoice.billing_profile);
lines.query();
if(lines.next()){
	template = template.replace('provisioning_contact_name', lines.name);
	template = template.replace('provisioning_phone_number', lines.mobile_phone);
	template = template.replace('provisioning_email_address', lines.email);
}
else {
	template = template.replace('provisioning_contact_name', ' ');
	template = template.replace('provisioning_phone_number', ' ');
	template = template.replace('provisioning_email_address', ' ');
}

var grAcc = invoice.client.getRefRecord();
	template = template.replace('right_company', grAcc.name);
	template = template.replace('right_address', grAcc.getDisplayValue('u_location'));
	template = template.replace('right_vat', grAcc.u_contribution_code_vat_cui);

var cmbId = gs.getProperty('x_tsigh_cmb.billing_address');
var cmbBill = new GlideRecord('customer_account');
cmbBill.addQuery('sys_id', cmbId);
cmbBill.query();
if(cmbBill.next()){
	template = template.replace('left_company', cmbBill.name);
	template = template.replace('left_address', cmbBill.getDisplayValue('u_location'));
	template = template.replace('left_vat', cmbBill.u_contribution_code_vat_cui);
}

var v = new sn_pdfgeneratorutils.PDFGenerationAPI;
var result = v.convertToPDF(template, tableName, invoice.sys_id, inputs.filename);

})(inputs, outputs);

