sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	current.price = Math.round(current.unit_price*current.quantity*100)/100;
	current.vat_value = Math.round(current.price*current.client_invoice.vat_value*100)/100;

	current.vat_value_in_ron = Math.round(current.vat_value * current.client_invoice.exchange_rate_value*100)/100;
	current.price_in_ron = Math.round(current.price * current.client_invoice.exchange_rate_value*100)/100;
	//current.unit_price_in_ron = current.unit_price * current.client_invoice.exchange_rate_value;
	var upr = Math.round(current.unit_price * current.client_invoice.exchange_rate_value*100)/100;
	current.unit_price_in_ron = upr;	
	var x = upr*(1+current.client_invoice.vat_value);
	current.unit_price_with_vat_in_ron = Number(Math.round(x + 'e2') + 'e-2');
	current.update();


	var gr = new GlideRecord('x_tsigh_cmb_client_invoice_lines');
	gr.addQuery('client_invoice',current.client_invoice);
	gr.query();
	var total = 0;
	while(gr.next()){
		total+=gr.price;
	}

	var gri = new GlideRecord('x_tsigh_cmb_client_invoice');
	gri.get(current.client_invoice.sys_id);
	var totalNet = Math.round(total*100)/100;
	gri.total_net_value = totalNet; //parseFloat(parseInt(total*100))/100;
	var totalVat = Math.round(totalNet*gri.vat_value*100)/100;
	gri.total_value_of_vat = totalVat;
	gri.total = totalNet+totalVat;
	gri.update();

})(current, previous);
