sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	current.total_value_of_vat = new x_tsigh_cmb.CMBInvoiceUtils().calculateVATValues(current);
	current.update();
	var total = 0;
	var totalvat = 0;
	if(current.currency.sys_id != previous.currency.sys_id || current.exchange_rate_value!= previous.exchange_rate_value){
		var gr = new GlideRecord('x_tsigh_cmb_client_invoice_lines');
		gr.addQuery('client_invoice', current.sys_id);
		gr.query();
		while(gr.next()){
			total+=gr.price;
			totalvat+=gr.vat_value;
			gr.vat_value_in_ron = Math.round(gr.vat_value * current.exchange_rate_value*100)/100;
			gr.price_in_ron = Math.round(gr.price * current.exchange_rate_value*100)/100;
			var upr = Math.round(gr.unit_price * current.exchange_rate_value*100)/100;
			gr.unit_price_in_ron = upr;
			gr.unit_price_with_vat_in_ron = upr *(1+current.vat_value);
			gr.update();		

		}

		var totalNet = Math.round(total*100)/100;
		current.total_net_value = totalNet;
		var totalVat = Math.round(totalNet*current.vat_value*100)/100;
		current.total_net_value_in_ron = Math.round(total*current.exchange_rate_value*100)/100;

		current.total_value_of_vat = totalvat;
		current.total_value_of_vat_in_ron = Math.round(totalvat*current.exchange_rate_value*100)/100;

		current.total = Math.round((totalNet+totalVat)*100)/100;		
		current.total_in_ron = Math.round(total*current.exchange_rate_value*100)/100
		current.update();


	}


})(current, previous);
