sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading ) {
		return;
	}
	if(newValue === ''){
		g_form.clearValue('quantity');
		g_form.clearValue('unit_price');
		g_form.clearValue('total_price');

		g_form.clearValue('contract_number');
		g_form.clearValue('start_date');
		g_form.clearValue('end_date');		



	}else {
		if(g_form.getReference('product',function(gr){
			switch(gr.getValue('sys_class_name')){
				case 'cmdb_contract_product_model':{
					g_form.setVisible('quantity',false);
					g_form.clearValue('quantity');

					g_form.setVisible('unit_price',false);
					g_form.clearValue('unit_price');

					g_form.setVisible('total_price',false);
					g_form.clearValue('total_price');

					g_form.setVisible('contract_number',true);
					g_form.setVisible('start_date',true);
					g_form.setVisible('end_date',true);
				}
					break;
				case 'cmdb_hardware_product_model':{
					g_form.setVisible('contract_number',false);
					g_form.clearValue('contract_number');

					g_form.setVisible('start_date',false);
					g_form.clearValue('start_date');

					g_form.setVisible('end_date',false);
					g_form.clearValue('end_date');

					g_form.setVisible('quantity',true);
					g_form.setVisible('unit_price',true);
					g_form.setVisible('total_price',true);					
				}
					break;
			}
		}));

	}

	//Type appropriate comment here, and begin script below

}
