sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    var ciRelType = gs.getProperty('x_tsigh_cmb.ci_rel_type_contains');
    var CIRelUtils = new x_tsigh_cmb.CIRelUtils();

    var a1 = new Array();
    a1 = previous.u_assets.toString().split(",");
    var a2 = new Array();
    a2 = current.u_assets.toString().split(",");


    if (a1.length > 0) {
        for (var i = 0; i < a1.length; i++) {
            if (a2.indexOf(a1[i]) === -1) {
                var result = CIRelUtils.delCIRel(current.sys_id, a1[i], ciRelType);
				result = CIRelUtils.setCIstatus(a1[i],'6'); // In Stock
				result = CIRelUtils.setAssetEQ(a1[i], '', '');
            }
        }
    }

    if (a2.length > 0) {
        for (var j = 0; j < a2.length; j++) {
            if (a1.indexOf(a2[j]) === -1) {
                var result2 = CIRelUtils.createCIRel(current.sys_id, a2[j], ciRelType);
				result2 = CIRelUtils.setCIstatus(a2[j],'1'); // Installed/In Production
				result2 = CIRelUtils.setAssetEQ(a2[j], current.sys_id, current.u_service);
            }
        }
    }


})(current, previous);
